import React from 'react';
import PropTypes from 'prop-types';
import { withRouter } from 'react-router-dom';
import { Card, Button, Grid, Segment, Header, Divider } from 'semantic-ui-react';


class BusinessItemWithoutRouter extends React.Component {
  static propTypes = {
    history: PropTypes.object.isRequired,
  }

  renderRating(ratingNumb) {
    return (
      <div className="ui accurate star widget inline" style={{ marginRight: '10px' }}>
        <div className="highlight" style={{ width: `${(ratingNumb / 5) * 100}%` }} />
      </div>);
  }

  render() {
    const avatarPlaceholder = 'data:image/jpeg;base64,';

    return (
      <Card fluid style={this.props.isNotList ? { height: '400px' } : null} onClick={() => { this.props.history.push(`/view-profile/business/${this.props.business.user.username}/`); }}>
        <Card.Content>
          <Grid>
            <Grid.Column mobile={16} tablet={8} computer={4}>
              <Segment
                textAlign="center"
                circular
                className="profilePicSegment"
                style={{
                  width: 175, height: 175, backgroundImage: `url(${this.props.business.company_logo ? `https://ipfs.io/ipfs/${this.props.business.company_logo}` : avatarPlaceholder})`, backgroundRepeat: 'no-repeat', backgroundSize: 'contain', backgroundPosition: 'center center',
                }}
              />
            </Grid.Column>
            <Grid.Column mobile={16} tablet={8} computer={12}>
              <Header size="large" style={{ textAlign: 'center' }}>
                {this.props.business.company_name}
              </Header>
              <p>
              Open Job Positions: {this.props.business.jobs_count}
              </p>
              <Divider clearing />
              <p style={{ wordBreak: 'break-word' }}>
                {this.props.business.company_about ? `${this.props.business.company_about.substring(0, 100)}...` : '-'}
              </p>
            </Grid.Column>
          </Grid>
        </Card.Content>
        <Card.Content extra>
          <Button name="course-page" onClick={() => { this.props.history.push(`/view-profile/bussiness/${this.props.business.user.username}/`); }}>
            Show Profile
          </Button>
        </Card.Content>
      </Card>
    );
  }
}

const BusinessItem = withRouter(BusinessItemWithoutRouter);

export default BusinessItem;
